import { useEffect, useState } from "react";

function PokemonView() {
  const [allPokemons, setAllPokemons] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(undefined);

  useEffect(() => {
    fetch("http://localhost:8080", {
      headers: { "Access-Control-Allow-Origin": "*" },
    })
      .then((response) => response.json())
      .then((data) => setAllPokemons(data));
  }, []);

  const getSinglePokemonInfo = event => {

  }

  const getAllPokemonDropdown = () => {
    return (
      <select onChange={() => getSinglePokemonInfo()}>
        <option value="default">--All Pokemons--</option>
        {allPokemons.map((pokemon) => (
          <option value={pokemon.name} key={pokemon.id}>
            {pokemon.name}
          </option>
        ))}
      </select>
    );
  };

  return <div>
    {getAllPokemonDropdown()}
    {selectedPokemon && getSinglePokemonInfo()}
    </div>;
}

export default PokemonView;
